widget.require:
HelloWorld = function() {};
HelloWorld.prototype.onLoad = function() {
	var thisWidget = this;
	$("#__WID__button").click(function(e) {
		e.preventDefault();
		thisWidget.OpenAjax.hub.publish("Sample.Hello", "Tere, maailm!");
	});
	// subscribe to Sample.Time
	this.OpenAjax.hub.subscribe("Sample.Time", function(topic, data) {
		$("#__WID__time").html(data);
	});
};
